const defaultState={
	value:'',
	list:[]
}
export default (state=defaultState,action)=>{
	if (action.type==='onchange') {
		const newState=JSON.parse(JSON.stringify(state));
		      newState.value=action.value;
		      return newState;
	};
	return state;
}
